// client/src/TradesList.js
import React, { useState, useEffect } from 'react';

function TradesList({ userId }) {
    const [trades, setTrades] = useState([]);
    const [error, setError] = useState('');
    const [message, setMessage] = useState('');

    const fetchTrades = async () => {
        try {
            const response = await fetch(`http://localhost:4000/game/all-trades`);
            const data = await response.json();

            if (response.ok) {
                setTrades(data);
                setError('');
            } else {
                setError(data.message || 'Failed to fetch trades');
            }
        } catch (error) {
            console.error('Error fetching trades:', error);
            setError('Network error');
        }
    };

    useEffect(() => {
        fetchTrades();
    }, [userId]);

    return (
        <div>
            <h3>Market Trades</h3>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {message && <p>{message}</p>}

            <table>
                <thead>
                <tr>
                    <th>User ID</th>
                    <th>Item</th>
                    <th>Price</th>
                    <th>Action</th>
                </tr>
                </thead>
                <tbody>
                {trades.map((trade, index) => (
                    <tr key={index}>
                        <td>{trade.userId}</td>
                        <td>{trade.trade}</td>
                        <td>{trade.price}</td>
                        <td>
                            <button
                                onClick={() => handleBuy(trade.userId, trade.trade)}
                                disabled={userId === trade.userId.toString()}
                            >
                                Buy
                            </button>
                        </td>
                    </tr>
                ))}
                </tbody>
            </table>
        </div>
    );
}

export default TradesList;
