// client/src/TradesList.js
import React, { useState, useEffect } from 'react';

function TradesList() {
    const [trades, setTrades] = useState([]);
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(true);

    const fetchTrades = async () => {
        try {
            const response = await fetch('http://localhost:4000/game/all-trades');
            const data = await response.json();

            if (response.ok) {
                setTrades(data);
            } else {
                setError(data.message || 'Failed to load trades');
            }
        } catch (err) {
            setError('Network error');
            console.error('Fetch error:', err);
        } finally {
            setIsLoading(false);
        }
    };

    useEffect(() => {
        fetchTrades();
    }, []);

    if (isLoading) {
        return <p>Loading trades...</p>;
    }

    if (error) {
        return <p style={{ color: 'red' }}>{error}</p>;
    }

    return (
        <div className="trades-container">
            <h3>Active Market Trades</h3>
            <table className="trades-table">
                <thead>
                <tr>
                    <th>Seller ID</th>
                    <th>Item</th>
                    <th>Price</th>
                </tr>
                </thead>
                <tbody>
                {trades.length > 0 ? (
                    trades.map((trade, index) => (
                        <tr key={index}>
                            <td>{trade.userId}</td>
                            <td>{trade.trade}</td>
                            <td>{trade.price.toFixed(2)}</td>
                        </tr>
                    ))
                ) : (
                    <tr>
                        <td colSpan="3">No active trades available</td>
                    </tr>
                )}
                </tbody>
            </table>
        </div>
    );
}

export default TradesList;
