// client/src/GameScreen.js
import React, { useState, useEffect } from 'react';
import ProfessionSelection from './ProfessionSelection';

function GameScreen({ userId }) {
    const [profession, setProfession] = useState('');
    const [resources, setResources] = useState({ resource1: 0, resource2: 0 });
    const [error, setError] = useState('');
    const ButtonClicked = UseRef
    const fetchUserData = async () => {
        try {
            const response = await fetch(`http://localhost:4000/game/resources/${userId}`);
            const data = await response.json();

            if (response.ok) {
                setResources(data);
                setProfession(data.profession);
            } else {
                setError(data.message || 'Failed to fetch resources');
            }
        } catch (error) {
            console.error('Error fetching resources:', error);
            setError('Network error');
        }
    };


    useEffect(() => {
        // Fetch user data (profession, resources) when component mounts
        fetchUserData();
    }, [userId]); // Run only when userId changes

    const handleProfessionChange = async (newProfession) => {
        try {
            const response = await fetch('http://localhost:4000/game/change-profession', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ userId, profession: newProfession })
            });

            const data = await response.json();

            if (response.ok) {
                setProfession(data.profession);
            } else {
                setError(data.message || 'Failed to change profession');
            }
        } catch (error) {
            console.error('Error changing profession:', error);
            setError('Network error');
        }
    };
    const Refresh = () => {
        fetchUserData();
    };

    return (
        <div>
            <h2>Game Screen</h2>
            {error && <p style={{color: 'red'}}>{error}</p>}
            <p>User ID: {userId}</p>
            <p>Profession: {profession || 'Unemployed'}</p>
            <p>Resource 1: {resources.resource1}</p>
            <p>Resource 2: {resources.resource2}</p>
            <button type="button" className="btn" onClick={Refresh}>обновить</button>
            <ProfessionSelection onChangeProfession={handleProfessionChange}/>

        </div>
    );
}

export default GameScreen;
