    const handleGoldExchange = (profession) => {
        onGoldExchange(profession);
    };

    return (
        <div>
            <h2>Resorce to gold</h2>
            <form onSubmit={handleGoldExchange}>
                <div>
                    <label htmlFor="username">Количество:</label>
                    <input type="number" id="numberInput" placeholder="Ресурсов на обмен"/>
