// client/src/GoldExchange.js
import React from 'react';

function GoldExchange({ onGoldExchange }) {
    const handleGoldExchange = (profession) => {
        onGoldExchange(profession);
    };

    return (
        <div>
            <h2>Resorce to gold</h2>
            {error && <p style={{ color: 'red' }}>{error}</p>}
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="username">Username:</label>
                    <input
                        type="text"
                        id="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                    <input type="number" id="numberInput" placeholder="Введите число здесь"/>
                </div>
                <div class="input-group">
                    <label for="dropdown">Выберите ресурс</label>
                    <select id="dropdown">
                        <option value="">-- Выберите вариант --</option>
                        <option value="Wood">Пункт 1</option>
                        <option value="Iron">Пункт 2</option>
                        <option value="Bread">Пункт 3</option>
                    </select>
                </div>
                <button type="submit">Exchange</button>
            </form>
        </div>
    );
}

export default GoldExchange;
